import React, { Component } from 'react';
import { GetAllOrders } from '../../Services/DB';

// let orders = { date, email, item, price, vendor };
class Detail extends Component {
    state = {
        order: []
    }

    componentDidMount() {

        //read email from props and store
        let request = {
            email: this.props.email,
            //by passing this method into the firebase method, we will use it to
            //receive data from the firebase cloud firestore
            callback: (docs) => {

                let results = []

                docs.forEach((doc) => {

                    const { date, email, price, item, vendor } = doc.data();

                    let orderItem = {
                        id: doc.id,
                        date: date,
                        email: email,
                        item: item,
                        price: price,
                        vendor: vendor,
                    }

                    results.push(orderItem);
                    //console.log(component_name, doc.id, " => ", doc.data());
                });

                results.forEach((item) => {
                    console.log(component_name, item.id, " => ", item.email);
                });

                this.setState(
                    {
                        order: results,
                    }
                );
            }
        }

        //call the DB method
        GetOrders(request);
    }
    render() {
        const { email, item, price, vendor, date } = this.props.orders;
        return (
            <table className="table container table-striped bg-secondary text-white detail">
                <thead>
                    <tr>
                        <th>Email:</th>
                        <th>Item:</th>
                        <th>Price:</th>
                        <th>Vendor:</th>
                        <th>Date:</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{email}</td>
                        <td>{item}</td>
                        <td>{price}</td>
                        <td>{vendor}</td>
                        <td>{date}</td>
                    </tr>
                </tbody>
            </table>
        )
    }
}

export default Detail;
