import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { GetOrder } from '../../Services/DB';

// let orders = { date, email, item, price, vendor };
class Detail extends Component {

    componentWillMount = () => {

        let request = {
            //by passing this method into the firebase method, we will use it to
            //receive data from the firebase cloud firestore
            callback: (docs) => {

                docs.forEach((doc) => {

                    const { vendor } = doc.data();

                    let docId = vendor.id;

                    console.log("Retreived DocId: ", docId);
                });
            }
        }
        console.log("Detail request: ", request);

        GetOrder(request);
    }
    render() {
        const { user, userAuth } = this.props;
        if (user) { return <Redirect to="/" /> };
        return (
            <table className="table container table-striped bg-secondary text-white detail">
                <thead>
                    <tr>
                        <th>Item:</th>
                        <th>Price:</th>
                        <th>Vendor:</th>
                        <th>Date:</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        {/* <td>{order.email}</td>
                        <td>{order.item}</td>
                        <td>{order.price}</td>
                        <td>{order.vendor.title}</td>
                        <td>{order.date}</td> */}
                    </tr>
                </tbody>
            </table>
        )
    }
}

export default Detail;
