import React, { Component } from 'react';
import SelectButton from '../components/selectbutton';

let selection = [];

class Card extends Component {
    state = {
        cards: {
            selected: false,
            item: '',
            selection: []
        }
    }

    handleSelection = () => {
        const selected = this.state.cards.selected;
        this.setState({
            cards: {
                selected: !selected
            }
        });
        (!selected) ? selection.push(this.props.title) : selection.pop(this.props.title);
        this.setState({ selection: selection });
        console.log(this.state.cards.selection);
    }

    // handleSelection = () => {

    //     selection.push(this.props.title);
    //     this.setState({ selection });
    //     console.log(selection);
    // }
    render() {
        const { title, image, cards } = this.props;
        return (
            <div className="card bg-secondary">
                <div className="card-body text-center">
                    <div className="card-title"><img className="img-thumbnail rounded" src={image} alt={title} /></div>
                    <h4 className="card-title bg">{title}</h4>
                    <SelectButton title={title} cards={cards}
                        onCardClick={this.handleSelection} />
                </div>
            </div>
        );
    }
}

export default Card;
