import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { GetOrder } from '../../Services/DB';

// let orders = { date, email, item, price, vendor };
class Detail extends Component {

    componentWillMount = () => {

        let request = {

            callback: (docs) => {

                let results = []

                docs.forEach((doc) => {

                    const { id } = doc.data();

                    let orderItem = {
                        id: doc.id,
                    }

                    results.push(orderItem);
                    //console.log(component_name, doc.id, " => ", doc.data());
                });

                results.forEach((item) => {
                    console.log(item.id, " => ", item.email);
                });

                this.setState(
                    {
                        orders: results,
                    }
                );
            }
        }

        GetOrder(request);
    }

    render() {
        const { user } = this.props;
        if (user) { return <Redirect to="/" /> };
        return (
            <table className="table container table-striped bg-secondary text-white detail">
                <thead>
                    <tr>
                        <th>Item:</th>
                        <th>Price:</th>
                        <th>Vendor:</th>
                        <th>Date:</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        {/* <td>{order.email}</td>
                        <td>{order.item}</td>
                        <td>{order.price}</td>
                        <td>{order.vendor.title}</td>
                        <td>{order.date}</td> */}
                    </tr>
                </tbody>
            </table>
        )
    }
}

export default Detail;
