import firebase from 'firebase';

const db = firebase.firestore();

/**
 * 
 * @param {order} order 
 */
const CompleteOrder = (order) => {

    const { email, item, date, vendor, price } = order;

    console.log("DB: ", `${email} # ${item} # ${date} # ${vendor} # ${price} #`)

    // Add a new document with a generated id.
    db.collection("orders").add({
        date: date,
        email: email,
        item: item,
        price: price,
        vendor: vendor
    })
        .then(function (docRef) {
            console.log("Document written with ID: ", docRef.id);
        })
        .catch(function (error) {
            console.error("Error adding document: ", error);
        });

};

const GetOrders = (inquiry) => {

    //stuff
    let ordersRef = db.collection("orders");

    ordersRef.where("email", "==", `${inquiry.email}`).get()
        .then((querySnapshot) => {
            if (!querySnapshot.empty) {
                inquiry.callback(querySnapshot);
            }
        })
        .catch((error) => {
            console.error("DB: ", error);
        });

};

export { CompleteOrder, GetOrders };
