    render() {
        const { user, userAuth } = this.props;
        const { order } = this.state;
        if (user) { return <Redirect to="/" /> };
        return (
            <table className="table container table-striped bg-secondary text-white detail">
                <thead>
                    <tr>
                        <th>Email:</th>
                        <th>Item:</th>
                        <th>Price:</th>
                        <th>Vendor:</th>
                        <th>Date:</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{order.email}</td>
                        <td>{order.item}</td>
                        <td>{order.price}</td>
                        <td>{order.vendor.title}</td>
                        <td>{order.date}</td>
                    </tr>
                </tbody>
            </table>
        )
    }
}

export default Detail;
